import './App.css'
import { useState } from "react";

const URL = "http://localhost:5000/tasks/";

function AddTask(text) {
  fetch(URL, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({ title: text }),
  })
  .then( response => {response.json(); })
  .then( data => { return data; });
}

async function GetAllTask(updateFunction) {
  const response = await fetch(URL);
  const data = await response.json()
  updateFunction(data);
  console.log(data)
}

export default function Board() {
  const [tasksToDisplay, setTasksToDisplay] = useState([]);
  const [title, setTitle] = useState("");  
  const handleChange = (evt) => {
    setTitle(evt.target.value);
  };

  return (
    <>
      <div>TODO LIST</div>
      <button className="roundBtn"> + </button>
    <form>
      <label htmlFor="name">Titre de la t√¢che</label>
      <input
        type="text"
        name="name"
        id="name"
        value={title}
        onChange={handleChange} />
      <button onClick={(e) => {
        e.preventDefault();
        AddTask(title);
      }}> Ajouter une tache </button>
      <button onClick={(e) => {
        e.preventDefault();
        GetAllTask({setTasksToDisplay});
      }}> trouver taches </button>
      {tasksToDisplay.map((task) => (
            <div key={task.id}> Titre : {task.title} </div>
        ))}
    </form>
    </>
  );
}
