import './App.css'
import { useEffect, useState } from "react";

const URL = "http://localhost:5000/tasks/";

function AddTask(text) {
  fetch(URL, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({ title: text }),
  })
  .then( response => {response.json(); })
  .then( data => { return data; });
}

async function GetAllTask({updateFunction}) {
  const response = await fetch(URL);
  const data = await response.json()
  updateFunction(data);
  console.log(data)
}

async function DeleteTask(id) {
  await fetch(`${URL}${id}`, {
    method: "DELETE",
    headers: {
      "Content-Type": "application/json",
    },
  })
}

async function DoneTask(task) {
  task.done = true;
  await fetch(`${URL}${task.id}`, {
    method: "PUT",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(task),
  })
  console.log(task);
}

export default function Board() {
  const [tasksToDisplay, setTasksToDisplay] = useState([]);
  const [title, setTitle] = useState("");  
  const handleChange = (evt) => {
    setTitle(evt.target.value);
  };

  useEffect(() => {
    GetAllTask({updateFunction: setTasksToDisplay});
  })

  return (
    <>
      <h1>TODO LIST</h1>
      <div className="addTask">
        <label htmlFor="name">Titre de la t√¢che</label>
        <input
          type="text"
          name="name"
          id="name"
          value={title}
          onChange={handleChange} />
        <button onClick={(e) => {
          e.preventDefault();
          AddTask(title);
        }}> Ajouter une tache </button>
      </div>
        <div className='grid'>

      {tasksToDisplay.map((task) => (
            <div key={task.id} className="taskCard"> 
            <div className='titre'>
              Titre : {task.title}
              {task.done ?
              <div > fait </div>
              : <></>
              } 
              </div>
              <button
                onClick={(e) => {
                e.preventDefault();
                DeleteTask(task.id);
              } }>
                Supprimer
              </button>
              <button
                onClick={(e) => {
                e.preventDefault();
                DoneTask(task);
              } }>
                Fait
              </button>
            </div>

        ))}
        </div>
    </>
  );
}
